CREATE DATABASE venteChaussure DEFAULT CHARACTER SET = utf8; USE venteChaussure; CREATE TABLE clients ( id int(11) unsigned NOT NULL AUTO_INCREMENT, nom varchar(255) NOT NULL DEFAULT '', prenom varchar(255) NOT NULL DEFAULT '', email varchar(255) NOT NULL DEFAULT '', PRIMARY KEY (id) ) ENGINE=InnoDB DEFAULT CHARSET=utf8; CREATE TABLE chaussures ( id int(11) unsigned NOT NULL AUTO_INCREMENT, modele varchar(255) NOT NULL DEFAULT '', marque varchar(255) NOT NULL DEFAULT '', couleur varchar(255) NOT NULL DEFAULT '', prix int(11) unsigned DEFAULT NULL, ) ENGINE=InnoDB DEFAULT CHARSET=utf8; CREATE TABLE commandes ( id int(11) unsigned NOT NULL AUTO_INCREMENT, adresseLivraison varchar(255) NOT NULL, clients_id int(11) unsigned NOT NULL, chaussures_id int(11) unsigned NOT NULL, quantite int(11) unsigned NOT NULL, pointure int(11) unsigned NOT NULL, CONSTRAINT fk_clients FOREIGN KEY (clients_id) REFERENCES clients (id), CONSTRAINT fk_chaussures FOREIGN KEY (chaussures_id) REFERENCES chaussures (id) ) ENGINE=InnoDB CHARSET=utf8; CREATE TABLE commandes_chaussures ( id_commandes int unsigned NOT NULL alter table commandes_chaussuresadd CONSTRAINT fk_idcommandes FOREIGN KEY (id_commandes) REFERENCES commandes (id); alter table commandes_chaussuresadd CONSTRAINT fdesk_idchaussures FOREIGN KEY (id_chaussures) REFERENCES chaussures (id); alter table commandes_chaussuresadd primary key (id_commandes,id_chaussures);
